    private void textemailActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_textemailActionPerformed
        // TODO add your handling code here:
        textcard.setText(textcard.getText());
        textname.setText(textname.getText());
        textadd.setText(textadd.getText());
        if(jRadioButton1.isSelected()){
            String Female = "Female";
        }
        else{
            String Male = "Male";
        }
        hp.setText(texthp.getText());
        email.setText(textcard.getText());

        textcard.setEnabled(false);
        textname.setEnabled(false);
        textadd.setEnabled(false);
        SGender.setEnabled(false);
        SStatus.setEnabled(false);
        hp.setEnabled(false);
        email.setEnabled(false);
    }//GEN-LAST:event_textemailActionPerformed

    private void submitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitActionPerformed
        // TODO add your handling code here:
        if(name.getText().equals("") || card.getText().equals("")|| jLabel2.getText().equals("")){
       this.setVisible(true);
    }else{
            javax.swing.JOptionPane.showMessageDialog(null,"Maaf Data Belum Lengkap");
            textcard.setText(card.getText());
textname.setText(name.getText());
textadd.setText(jLabel2.getText());
            }
        PembayaranDream bayar = new PembayaranDream();
        bayar.setVisible(true);
        this.dispose();
    }//GEN-LAST:event_submitActionPerformed

